import React from 'react'
import './style.css'

export default class EnterNewTitle extends React.Component{
    constructor(){
        super()
        this.state = {
            inputValue : ''
        }
    }
    onChangeHandler = (e) => {
        this.setState({
            inputValue: e.target.value
        })
    }
    handleSubmit = (value) => {
        fetch(`https://jsonplaceholder.typicode.com/photos/${this.props.match.params.id}`,
            {method: "PUT",
            body: JSON.stringify({ title: this.state.inputValue }),
            headers: {
                "Content-Type": "application/json"
            }}
        )
    }
    render(){
        console.log(this.state.inputValue)
        return (
            <div className='input-and-save'>
                <form onSubmit={this.handleSubmit(this.state.inputValue)}>
                    <input onChange={this.onChangeHandler} className='input-title' type='text' placeholder='Enter the new title here...' />
                    <button className='save-btn'>Save changes</button>
                </form>
            </div>
        )
    }
}
