import React from 'react'
import { Modal, Button } from 'react-bootstrap'
import { connect } from 'react-redux'
import { closeAddNewPhotoAction } from '../../store/actions/addNewPhotoAction'

class AddNewPhoto extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            title : '',
            thumbnailUrl : '',
            photoUrl : ''
        }
    }
    handleChange = (e) => {
        this.setState({
            [e.target.id] : e.target.value
        })
    }
    render (){
        console.log(this.state)
        return (
            <Modal show={this.props.show} onHide={this.props.handleClose}>
               <Modal.Header closeButton>
                   <Modal.Title>Add New Photo</Modal.Title>
               </Modal.Header>
               <Modal.Body>
                   <div className='body-container'>
                       <div className='label-and-input'>
                            <span className='label'>Title:</span>
                            <input id='title' className='input' onChange={this.handleChange} type='text' placeholder='Enter photo title...' />
                       </div>
                       <div className='label-and-input'>
                            <span className='label'>Thumbnail URL:</span>
                            <input className='input' onChange={this.handleChange} type='text' placeholder='Enter the thumbnail url here...' />
                       </div>
                       <div className='label-and-input'>
                            <span className='label'>Photo URL:</span>
                            <input className='input' onChange={this.handleChange} type='text' placeholder='Enter the photo url here...' />
                       </div>
                       <Button variant="outline-dark">
                               Submit
                       </Button>
                   </div>
               </Modal.Body>
               <Modal.Footer>
                   <Button variant="outline-dark" >
                           Close
                   </Button>
               </Modal.Footer>
            </Modal>
        )
    } 
}
const mapDispatchToProps = (dispatch) => {
    return {
        handleClose: () => {
            dispatch(closeAddNewPhotoAction())
        }
    }
}
export default connect(null, mapDispatchToProps)(AddNewPhoto)
