import React, { Component } from 'react'

export default class PhotoDetails extends Component {
    constructor(props){
        super(props)
        this.state = {
            photo: {}
        }
    }
    componentDidMount(){
        fetch(`https://jsonplaceholder.typicode.com/photos/${this.props.match.params.id}`,
        {method: 'get'})
        .then(res => res.json())
        .then(data => {
            this.setState({
                photo: data
            })
        })
    }
    render() {
        console.log(this.state.photo)
        return (
            <div className='photo-details-container'>
                <span>Photo Details</span>
                <div className='photo-and-details-container'>
                    <img src={this.state.photo.url} alt='slika'></img>

                    <div className='details-container'>
                        <span>Title:</span>
                        <span>{this.state.photo.title}</span>
                        <span>Album ID:</span>
                        <span>{this.state.photo.albumId}</span>
                        <span>Photo url:</span>
                        <span>{this.state.photo.url}</span>
                        <span>Thumbnail url:</span>
                        <span>{this.state.photo.thumbnailUrl}</span>
                    </div>
                </div>
            </div>
        )
    }
}
